/**
 * Local Business Website - Main JavaScript
 * Generated by Claude Local Business Workflow
 */

document.addEventListener('DOMContentLoaded', function() {

    // ========================================
    // FAQ Accordion
    // ========================================
    const faqItems = document.querySelectorAll('.faq-item');

    faqItems.forEach(item => {
        const question = item.querySelector('.faq-question');

        question.addEventListener('click', () => {
            // Close other items
            faqItems.forEach(otherItem => {
                if (otherItem !== item) {
                    otherItem.classList.remove('active');
                }
            });

            // Toggle current item
            item.classList.toggle('active');
        });
    });

    // ========================================
    // Mobile Menu Toggle
    // ========================================
    const menuToggle = document.querySelector('.menu-toggle');
    const mainMenu = document.querySelector('.main-menu');

    if (menuToggle && mainMenu) {
        menuToggle.addEventListener('click', () => {
            mainMenu.classList.toggle('active');
            menuToggle.classList.toggle('active');
        });
    }

    // ========================================
    // Smooth Scroll for Anchor Links
    // ========================================
    document.querySelectorAll('a[href^="#"]').forEach(anchor => {
        anchor.addEventListener('click', function(e) {
            e.preventDefault();
            const target = document.querySelector(this.getAttribute('href'));
            if (target) {
                target.scrollIntoView({
                    behavior: 'smooth',
                    block: 'start'
                });
            }
        });
    });

    // ========================================
    // Form Submission Handler
    // ========================================
    const forms = document.querySelectorAll('form');

    forms.forEach(form => {
        form.addEventListener('submit', async function(e) {
            e.preventDefault();

            const submitBtn = form.querySelector('button[type="submit"]');
            const originalText = submitBtn.textContent;

            // Show loading state
            submitBtn.textContent = 'Küldés...';
            submitBtn.disabled = true;

            try {
                const formData = new FormData(form);
                const response = await fetch(form.action, {
                    method: 'POST',
                    body: formData
                });

                if (response.ok) {
                    // Success
                    alert('Köszönjük! Üzenetét megkaptuk, hamarosan felvesszük Önnel a kapcsolatot.');
                    form.reset();
                } else {
                    throw new Error('Hiba történt');
                }
            } catch (error) {
                alert('Hiba történt az üzenet küldésekor. Kérjük, próbálja újra később, vagy hívjon minket telefonon.');
            } finally {
                submitBtn.textContent = originalText;
                submitBtn.disabled = false;
            }
        });
    });

    // ========================================
    // Lazy Loading Images
    // ========================================
    if ('loading' in HTMLImageElement.prototype) {
        // Browser supports native lazy loading
        const lazyImages = document.querySelectorAll('img[loading="lazy"]');
        lazyImages.forEach(img => {
            img.src = img.dataset.src || img.src;
        });
    } else {
        // Fallback for older browsers
        const lazyImages = document.querySelectorAll('img[loading="lazy"]');

        const lazyLoad = () => {
            lazyImages.forEach(img => {
                if (img.getBoundingClientRect().top < window.innerHeight + 100) {
                    img.src = img.dataset.src || img.src;
                    img.removeAttribute('loading');
                }
            });
        };

        window.addEventListener('scroll', lazyLoad);
        lazyLoad();
    }

    // ========================================
    // Phone Number Click Tracking (Analytics)
    // ========================================
    document.querySelectorAll('a[href^="tel:"]').forEach(link => {
        link.addEventListener('click', function() {
            if (typeof gtag === 'function') {
                gtag('event', 'click', {
                    'event_category': 'Contact',
                    'event_label': 'Phone Call',
                    'value': this.getAttribute('href')
                });
            }
        });
    });

    // ========================================
    // Email Link Click Tracking (Analytics)
    // ========================================
    document.querySelectorAll('a[href^="mailto:"]').forEach(link => {
        link.addEventListener('click', function() {
            if (typeof gtag === 'function') {
                gtag('event', 'click', {
                    'event_category': 'Contact',
                    'event_label': 'Email',
                    'value': this.getAttribute('href')
                });
            }
        });
    });

    // ========================================
    // Cookie Consent
    // ========================================
    const cookieConsent = document.getElementById('cookie-consent');
    const cookieAccept = document.getElementById('cookie-accept');
    const cookieSettings = document.getElementById('cookie-settings');
    const cookieSettingsBtn = document.getElementById('cookie-settings-btn');

    // Check if consent was already given
    const hasConsent = localStorage.getItem('cookieConsent');

    if (cookieConsent) {
        if (!hasConsent) {
            // Show cookie consent banner after a short delay
            setTimeout(() => {
                cookieConsent.classList.add('show');
            }, 1000);
        } else {
            // Show settings button if consent was given
            if (cookieSettingsBtn) {
                cookieSettingsBtn.style.display = 'block';
            }
        }

        // Accept cookies
        if (cookieAccept) {
            cookieAccept.addEventListener('click', () => {
                localStorage.setItem('cookieConsent', 'accepted');
                localStorage.setItem('cookieConsentDate', new Date().toISOString());
                cookieConsent.classList.remove('show');
                if (cookieSettingsBtn) {
                    cookieSettingsBtn.style.display = 'block';
                }

                // Track consent in analytics if available
                if (typeof gtag === 'function') {
                    gtag('consent', 'update', {
                        'analytics_storage': 'granted'
                    });
                }
            });
        }

        // Settings button (for now just shows the banner again)
        if (cookieSettings) {
            cookieSettings.addEventListener('click', () => {
                // For a simple implementation, redirect to privacy policy
                window.location.href = '/adatvedelmi-nyilatkozat/';
            });
        }

        // Cookie settings button (bottom left)
        if (cookieSettingsBtn) {
            cookieSettingsBtn.addEventListener('click', () => {
                cookieConsent.classList.add('show');
                cookieSettingsBtn.style.display = 'none';
            });
        }
    }

    // ========================================
    // Close Mobile Menu on Link Click
    // ========================================
    const menuLinks = document.querySelectorAll('.main-menu a');
    menuLinks.forEach(link => {
        link.addEventListener('click', () => {
            if (mainMenu && mainMenu.classList.contains('active')) {
                mainMenu.classList.remove('active');
                if (menuToggle) {
                    menuToggle.classList.remove('active');
                }
            }
        });
    });

});
